#!/bin/env/python"""#Define atom class:    Attributes: atomid (id for the atom), element (C, N, O ...), altid (id within residue), restyp (residue type),     chainid (chain id), seqid (residue number), x, y, z (location), occ (occupancy), biso (b factor), xyz_change (movement compared to another pdb)    """    class Atom:    def __init__(self, atomid, element, altid, restyp, chainid, seqid, x, y, z, occ, biso, xyz_change):        self.atomid = atomid        self.element = element        self.altid = altid        self.restyp = restyp        self.chainid = chainid        self.seqid = seqid        self.x = x        self.y = y        self.z = z        self.occ = occ        self.bsio = biso        self.xyz_change = xyz_change